}

/* Archives*/
.boilerplate {
    background-color: var(--dc_color_23) !important;
    border-color: var(--dc_color_24) !important;
}

/* Tooltip */
.referencetooltip li,
.oo-ui-popupWidget-popup,
.oo-ui-popupToolGroup .oo-ui-toolGroup-tools {
    background-color: var(--dc_color_17) !important;
    border-color: var(--dc_color_22) !important;
}

.referencetooltip li+li {
    background-color: var(--dc_color_00) !important;
    border-top-color: var(--dc_color_22) !important;
    border-right-color: var(--dc_color_00) !important;
    border-left-color: var(--dc_color_00) !important;
}
.oo-ui-popupWidget-anchored-top .oo-ui-popupWidget-anchor:before,
.oo-ui-popupWidget-anchored-top .oo-ui-popupWidget-anchor:after {
    border-bottom-color: var(--dc_color_22) !important;
}

/* Button */
.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button,
.ve-ui-mwParameterResultWidget-name,
.mw-ui-button,
.wikibase-entitytermsview .wikibase-toolbar-container {
    background-color: var(--dc_color_17) !important;
    border-color: var(--dc_color_17) !important;
    color: var(--dc_color_07) !important;
}

.oo-ui-buttonElement-framed.oo-ui-widget-enabled > .oo-ui-buttonElement-button:hover,
.mw-ui-button:hover,
.oo-ui-messageDialog-actions .oo-ui-actionWidget.oo-ui-widget-enabled .oo-ui-buttonElement-button:hover {
    background-color: var(--dc_color_05) !important;
    border-color: var(--dc_color_05) !important;
    color: var(--dc_color_25) !important;
}

/* Main button */
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button,
.lang-list-button,
.pure-button-primary-progressive,
.pure-button {
    background-color: var(--dc_color_22) !important;
    border-color: var(--dc_color_22) !important;
    color: var(--dc_color_07) !important;
}
.oo-ui-buttonElement-framed.oo-ui-widget-enabled.oo-ui-flaggedElement-primary.oo-ui-flaggedElement-progressive > .oo-ui-buttonElement-button:hover,
.lang-list-button:hover,
.pure-button-primary-progressive:hover,
.pure-button:hover {
    background-color: var(--dc_color_28) !important;
    border-color: var(--dc_color_28) !important;
    color: var(--dc_color_25) !important;
}

/* Forms input */
.pure-form input[type=search],
#simpleSearch,
.oo-ui-textInputWidget .oo-ui-inputWidget-input {
    background-color: var(--dc_color_07);
    color: var(--dc_color_14);
    border-color: var(--dc_color_02);
    border-radius: 0;
}

/* Warning notice */
.warningbox {
    color: var(--dc_color_14);
    border-color: var(--dc_color_27);
    background-color: var(--dc_color_29);
}

/* Code */
code {
    color: var(--dc_color_30) !important;
    background-color: var(--dc_color_02) !important;
    border-color: var(--dc_color_02) !important;
}

