}
.WatchNext-still-hover-container .PlayIcon {
    background-image: var(--play-icon) !important;
    background-size: 40px 50px !important;
    background-position: 18px 10px;
    background-repeat: no-repeat;
    box-sizing: border-box;
    display: block;
    width: 80px;
    height: 80px;
    padding-top: 14px;
    padding-left: 8px;
    line-height: 20px;
    border: 6px solid #fff;
    border-radius: 50%;
