ytd-button-renderer.style-blue-text[is-paper-button],
ytd-button-renderer.style-blue-text[is-icon-button],
yt-button-renderer.style-blue-text[is-paper-button],
yt-button-renderer.style-brand[is-paper-button],
#menu-container.ytd-video-primary-info-renderer,
yt-icon.checked.ytd-backstage-poll-renderer,
.guide-entry-badge.ytd-guide-entry-renderer,
.style-default-active + #vote-count-middle,
paper-radio-button[checked],
.ytp-volume-slider-handle,
paper-checkbox[checked],
#main.ytd-commentbox,
sup.ytp-swatch-color {
	animation: ColorTransition var(--color-transition-duration) linear infinite;
}

/* SVG Fill Color Animation */
.ytp-large-play-button.ytp-touch-device .ytp-large-play-button-bg,
.ytp-cued-thumbnail-overlay:hover .ytp-large-play-button-bg,
#heart-button button[aria-label="Remove heart"] yt-icon,
#hearted.ytd-creator-heart-renderer,
path[fill="#FF0000"],
path[fill="#ff0000"],
path[fill="#f80000"],
path[fill="#FF8983"],
path[fill="#f00"],
path[fill="#F00"],
circle[fill=red] {
	animation: FillTransition var(--color-transition-duration) linear infinite
}


/* Forcing a default color on the like bar container */
#sentiment.ytd-video-primary-info-renderer #container.ytd-sentiment-bar-renderer {
	background-color: #aaa !important
}

[dark] #sentiment.ytd-video-primary-info-renderer #container.ytd-sentiment-bar-renderer {
	background-color: #666 !important
}


/* Making sure that the animation isn't applied on non-red elements */
#main.ytd-commentbox .underline.is-highlighted.paper-input-container:not([v2]).paper-input-container .focused-line.paper-input-container,
#channel.ytd-grid-channel-renderer paper-button.ytd-subscribe-button-renderer:not([subscribed]),
ytd-mini-channel-renderer paper-button.ytd-subscribe-button-renderer:not([subscribed]),
ytd-mini-channel-renderer paper-button.ytd-subscribe-button-renderer::before {
	animation: none !important
}

/* Ensuring `background-color` is always the same as the `currentcolor` for synchronization of animation */
#main.ytd-commentbox .underline.is-highlighted.paper-input-container:not([v2]).paper-input-container .focused-line.paper-input-container,
ytd-sentiment-bar-renderer #like-bar.ytd-sentiment-bar-renderer,
ytd-button-renderer.style-suggestive[is-paper-button]::before,
.ytp-volume-slider-handle::before,
#checkbox.checked.paper-checkbox,
paper-ripple[animating] #waves,
#submit-button.ytd-commentbox,
#onRadio.paper-radio-button,
.ytp-volume-slider-handle {
	background-color: currentcolor !important
}

/* Ensuring `border-color` is always the same as the `currentcolor` for synchronization of animation */
ytd-button-renderer.style-suggestive[is-paper-button] paper-button.ytd-button-renderer,
.paper-input-container[v2] .focused-line.paper-input-container,
.badge-style-type-live-now.ytd-badge-supported-renderer,
ytd-button-renderer.style-suggestive[is-paper-button],
#checkbox.checked.paper-checkbox,
#offRadio.paper-radio-button {
	border-color: currentcolor !important
}

/* Ensuring `color` is always inherited for synchronization of animation */
#main.ytd-comment-box .underline.is-highlighted.paper-input-container:not([v2]).paper-input-container .focused-line.paper-input-container,
ytd-toggle-button-renderer.style-default-active[is-icon-button] ytd-formatted-string,
ytd-toggle-button-renderer.style-default-active[is-icon-button],
#ink.paper-radio-button[checked],
#submit-button.ytd-commentbox,
#ink.paper-checkbox[checked] {
	color: inherit !important
}

/* Ensuring `fill` is always the same as the `currentcolor` for synchronization of animation */
ytd-toggle-button-renderer.style-default-active[is-icon-button] yt-icon {
	fill: currentcolor !important
}


/* Forcing `color` to be white */
[dark] paper-item.ytd-backstage-poll-renderer[selected] .text-area.ytd-backstage-poll-renderer,
.badge-style-type-featured.ytd-badge-supported-renderer,
ytd-button-renderer.style-primary[is-paper-button],
#submit-button.ytd-commentbox yt-formatted-string,
button[aria-label="More actions"] + paper-ripple {
	color: #fff !important
}

/* Forcing translucent white text color */
ytd-subscribe-button-renderer[new-subscribe-color] paper-button.ytd-subscribe-button-renderer[subscribed],
paper-button.ytd-subscribe-button-renderer[subscribed] .deemphasize.yt-formatted-string,
paper-button.ytd-subscribe-button-renderer[subscribed],
.ytp-sb-unsubscribe {
	color: #fffc
}

[dark] .more-button-exp.ytd-comment-renderer:hover,
[dark] .more-button-exp.ytd-comment-renderer:focus,
[dark] .less-button-exp.ytd-comment-renderer:hover,
[dark] .less-button-exp.ytd-comment-renderer:focus,
[dark] .more-button-exp.ytd-comment-renderer,
[dark] .less-button-exp.ytd-comment-renderer,
[dark] paper-radio-button {
	color: #fff8
}

.more-button-exp.ytd-comment-renderer:hover,
.more-button-exp.ytd-comment-renderer:focus,
.less-button-exp.ytd-comment-renderer:hover,
.less-button-exp.ytd-comment-renderer:focus,
.more-button.ytd-comment-replies-renderer,
.less-button.ytd-comment-replies-renderer,
.more-button-exp.ytd-comment-renderer,
.less-button-exp.ytd-comment-renderer,
paper-radio-button {
	color: #888
}

.more-button-exp.ytd-comment-replies-renderer,
.less-button-exp.ytd-comment-replies-renderer {
	color: var(--yt-spec-text-secondary)
}

/* Making the paper ripple properly translucent and unobtrusive */
paper-ripple[animating] #waves {
	opacity: 0.15
}

/* Fixing the layering of the volume slider */
.ytp-volume-slider-handle::before {
	z-index: -1
}

/* Added theming to the `COMPACT_GRAY` subscribe buttons */
ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer,
ytd-toggle-button-renderer.style-compact-gray[is-paper-button] {
	background-color: #eee !important;
  color: #1119 !important;
}

[dark] ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer,
[dark] ytd-toggle-button-renderer.style-compact-gray[is-paper-button] {
	background-color: #222 !important;
  color: #eee9 !important;
}

ytd-subscribe-button-renderer[button-style="COMPACT_GRAY"] paper-button.ytd-subscribe-button-renderer::before {
	display: none;
}

/* Defaut styling for both "SUBSCIBE" and "VOTE" buttons */
paper-button.ytd-subscribe-button-renderer[subscribed],
ytd-button-renderer.style-suggestive[is-paper-button],
.ytp-sb-unsubscribe {
	background: transparent;
	position: relative;
	overflow: hidden
}

paper-button.ytd-subscribe-button-renderer[subscribed]::before, 
ytd-button-renderer.style-suggestive[is-paper-button]::before,
.ytp-sb-unsubscribe::before {
	content: '';
	display: block;
	height: 100%;
	position: absolute;
	width: 100%
}

/* Making the "SUBSCRIBE" button have `BgTransition` at 50% brightness when subscribed */
paper-button.ytd-subscribe-button-renderer[subscribed]::before,
.ytp-sb-unsubscribe::before  {
	left: 0;
	top: 0;
	filter: brightness(50%);
	z-index: -1
}
  
.ytp-sb-unsubscribe {
  z-index: 1
}

/* Making the "VOTE NOW" and "VIEW RESULTS" button background translucent */
ytd-button-renderer.style-suggestive[is-paper-button]::before {
	opacity: 0.075
}

/* Like/dislike bar ratio */
#sentiment.ytd-video-primary-info-renderer:not([activated_]) {
	color: #777 !important
}

[dark] #sentiment.ytd-video-primary-info-renderer:not([activated_]) {
	color: #aaa !important
}

/* Fixes */
.badge-style-type-verified-artist.ytd-badge-supported-renderer yt-icon,
.badge-style-type-verified.ytd-badge-supported-renderer yt-icon {
	width: var(--ytd-badge-icon-size, 13px);
  height: var(--ytd-badge-icon-size, 13px);
	color: var(--ytd-badge-icon-color, var(--ytd-owner-badge-color))
}

#metadata-line.ytd-grid-video-renderer :first-child::after {
	content: 'â€¢';
  margin: 0 4px
}

ytd-guide-renderer .paper-item.paper-item:focus:before,
ytd-guide-renderer paper-item:focus:before {
	opacity: 0 !important
}

[dark] .dropdown-content.paper-menu-button {
	background-color: transparent !important;
}

[dark] #hearted-border.ytd-creator-heart-renderer {
	fill: var(--app-drawer-content-container_-_background-color);
}

/* Flattube Compatibility */
.ytp-volume-slider-handle::after {
	background-color: #fff4 !important;
	opacity: 1;
	z-index: -1
}


/* "WATCHED" overlay */
yt-img-shadow.ytd-thumbnail[loaded] + #overlays.ytd-thumbnail {
	position: relative;
	width: 100%;
	height: 100%;
	display: flex;
	align-items: flex-end
}

ytd-thumbnail-overlay-resume-playback-renderer {
	position: static;
	width: 100%
}

#progress.ytd-thumbnail-overlay-resume-playback-renderer[style="width: 100%;"]::before {
	content: 'WATCHED';
	position: absolute;
	left: 0;
	top: 0;
	right: 0;
	bottom: 0;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 1.5em;
	font-weight: 500;
	letter-spacing: 0.025em;
	background-color: rgba(0, 0, 0, 0.8);
	color: #fff;
	z-index: -1;
	transition: opacity 250ms var(--standard-ease)
}

#overlays:hover #progress.ytd-thumbnail-overlay-resume-playback-renderer[style="width: 100%;"]::before {
	opacity: 0
}

ytd-thumbnail-overlay-playback-status-renderer {
	display: none;
}

sourceCode:
/* ==UserStyle==
@name YouTube DeepDark Material
@namespace gitlab.com/RaitaroH/YouTube-DeepDark
@homepageURL https://github.com/RaitaroH/YouTube-DeepDark
@version 3.4.3
@updateURL https://raw.githubusercontent.com/RaitaroH/YouTube-DeepDark/master/YouTubeDeepDarkMaterial.user.css
@description Videos should only be watched in the dark. May the dark be kinder on thine eyes. (YouTube dark theme)
@author RaitaroH
@license GNU-V3.0

@preprocessor stylus

@var select stylus-deepdark-style "Preset themes" [
	"Deep-Dark",
	"Inspired-Dark",
	"Breeze-Dark",
	"HavocOS",
	"Arc-Dark",
	"Vertex-Dark",
	"Mint-Y-Dark",
	"Adapta-Nokto",
	"Adapta-Breath-Nokto",
	"Gruvbox-Dark",
	"Gruvbox-Light",
	"NierAutomata-Dark",
	"NierAutomata-Light",
	"Solarized-Dark",
	"Solarized-Light",
	"9anime",
	"Firefox-Dark",
	"Firefox-57",
	"Discord",
	"YouTube-Dark",
	"Black-and-White",
	"Yellow",
	"Yellow-2",
	"Ubuntu-Grey",
	"Ubuntu-Purple",
	"Orange",
	"Jisho",
	"Custom"
]

@var checkbox join-color "Use dark join button" 0
@var checkbox subscribe-color "Use dark subscribe button" 0

@var color mainColor "Custom main color" "#367bf0"
@var color mainBackground "Custom main background" "#22242d"
@var color secondBackground "Custom second background" "#242730"
@var color hoverBackground "Custom hover background" "#4e5467"
@var color mainText "Custom main text" "#eee"
@var color dimmerText "Custom second text" "#ccc"
@var color colorShadow "Custom shadow color" "rgba(56, 60, 74, .3)"

==/UserStyle== */

@-moz-document regexp('https?:\/\/(?!gaming\.)(?!studio\.).*youtube.*\.com\/(?!premium).*$') {
	/* Author     : Theme made by RaitaroH */
	/* Co-authors : https://github.com/MechaLynx https://github.com/MaximeRF */
	/* Home       : https://github.com/RaitaroH/YouTube-DeepDark */
	/* Licence    : GNU General Public License v3.0 */

	/*Main color variables*/
	:root
	{
		if stylus-deepdark-style == "Deep-Dark" {
			--main-color: #00adee;
			--main-background: #111;
			--second-background: #181818;
			--hover-background: #232323;
			--main-text: #eff0f1;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .3);
		}
		else if stylus-deepdark-style == "Inspired-Dark" {
			--main-color: #5e8acc;
			--main-background: #232629;
			--second-background: #181818;
			--hover-background: #515254;
			--main-text: #eee;
			--dimmer-text: #ccc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Breeze-Dark" {
			--main-color: #3daee9;
			--main-background: #232629;
			--second-background: #2a2e32;
			--hover-background: #31363b;
			--main-text: #eff0f1;
			--dimmer-text: #bdc3c7;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Vertex-Dark" {
			--main-color: #4080fb;
			--main-background: #2b2b2c;
			--second-background: #353638;
			--hover-background: #515254;
			--main-text: #f3f3f5;
			--dimmer-text: #aeafb0;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Arc-Dark" {
			--main-color: #5294e2;
			--main-background: #343944;
			--second-background: #383c4a;
			--hover-background: #414a59;
			--main-text: #c1c8d1;
			--dimmer-text: #b3bac5;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-Dark" {
			--main-color: #5675b9;
			--main-background: #272b35;
			--second-background: #181d20;
			--hover-background: #353a44;
			--main-text: #e3eef9;
			--dimmer-text: #bec0cc;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "Firefox-57" {
			--main-color: #4080fb;
			--main-background: #0c0c0d;
			--second-background: #252526;
			--hover-background: #323234;
			--main-text: #f9f9fa;
			--dimmer-text: #d0d0d0;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style ==  "Discord" {
			--main-color: #7289da;
			--main-background: #1e2124;
			--second-background: #2f3136;
			--hover-background: #484b51;
			--main-text: #fff;
			--dimmer-text: #ada8aa;
			--shadow: 0 1px 0.5px rgba(47, 49, 54, .23);
		}
		else if stylus-deepdark-style == "YouTube-Dark" {
			--main-color: #e52117;
			--main-background: #111;
			--second-background: #232323;
			--hover-background: #343434;
			--main-text: #e1e1e1;
			--dimmer-text: #7f7f7f;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .2);
		}
		else if stylus-deepdark-style == "Mint-Y-Dark" {
			--main-color: #9ab87c;
			--main-background: #2f2f2f;
			--second-background: #383838;
			--hover-background: #404040;
			--main-text: #fff;
			--dimmer-text: #d5dada;
			--shadow: 0 1px 0.5px rgba(0, 0, 0, .13);
		}
		else if stylus-deepdark-style == "9anime" {
			--main-color: #723f8c;
			--main-background: #0b0a0d;
			--second-background: #17151c;
			--hover-background: #1E1c25;
			--main-text: #f9f6fb;
			--dimmer-text: #cac0cf;
			--shadow: 0 1px 0.5px rgba(54, 54, 54, .13);
		}
		else if stylus-deepdark-style == "Black-and-White" {
			--main-color: #fff;
			--main-background: #000;
			--second-background: #1e1e1e;
			--hover-background: #313131;
			--main-text: #fff;
			--dimmer-text: #aaa;
			--shadow: 0 1px 0.5px rgba(54 ,54 ,54, .2);
		}
		else if stylus-deepdark-style == "Yellow" {
			--main-color: #ffc700;
			--main-background: #141414;
			--second-background: #202222;
			--hover-background: #353838;
			--main-text: #eff0f1;
			--dimmer-text: #9f9999;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Yellow-2" {
			--main-color: #ffc700;
			--main-background: #0a0800;
			--second-background: #0c0a04;
			--hover-background: #0f0d05;
			--main-text: #fffdf5;
			--dimmer-text: #fff8e1;
			--shadow: 0 1px 0.5px rgba(34, 34, 34, .2);
		}
		else if stylus-deepdark-style == "Ubuntu-Grey" {
			--main-color: #ef7847;
			--main-background: #312d2a;
			--second-background: #3d3c38;
			--hover-background: #59564d;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(89, 86, 77, .12);
		}
		else if stylus-deepdark-style == "Ubuntu-Purple" {
			--main-color: #ef7847;
			--main-background: #2c071a;
			--second-background: #430b28;
			--hover-background: #520D30;
			--main-text: #f2f1ef;
			--dimmer-text: #e6e5e3;
			--shadow: 0 1px 0.5px rgba(82, 13, 48, .2);
		}
		else if stylus-deepdark-style == "Orange" {
			--main-color: #ff6905;
			--main-background: #0a0400;
			--second-background: #0e0702;
			--hover-background: #110903;
			--main-text: #fff9f5;
			--dimmer-text: #ffede1;
			--shadow: 0 1px 0.5px rgba(255, 105, 5, .1);
		}
		else if stylus-deepdark-style == "Jisho" {
			--main-color: #ef7d6c;
			--main-background: #332222;
			--second-background: #2a1b1b;
			--hover-background: #863b2f;
			--main-text: #EFB26C;
			--dimmer-text: #986E3F;
			--shadow: 0 1px 0.5px rgba(37, 19, 5, .19);
		}
		else if stylus-deepdark-style == "Adapta-Nokto" {
			--main-color: #00bcd4;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(61, 77, 86, .2);
		}
		else if stylus-deepdark-style == "Adapta-Breath-Nokto" {
			--main-color: #1abc9c;
			--main-background: #222d32;
			--second-background: #263238;
			--hover-background: #2a353b;
			--main-text: #fff;
			--dimmer-text: #9b9b9b;
			--shadow: 0 1px 0.5px  rgba(42, 53, 59, .32);
		}
		else if stylus-deepdark-style == "Solarized-Dark" {
			--main-color: #268bd2;
			--main-background: #073642;
			--second-background: #03303c;
			--hover-background: #002b36;
			--main-text: #fdf6E3;
			--dimmer-text: #eee8d5;
			--shadow: 0 1px 0.5px  rgba(0, 43, 54, .3);
		}
		else if stylus-deepdark-style == "Solarized-Light" {
			--main-color: #268bd2;
			--main-background: #fdf6e3;
			--second-background: #f5efdc;
			--hover-background: #eee8d5;
			--main-text: #073642;
			--dimmer-text: #586e75;
			--shadow: 0 1px 0.5px  rgba(222, 216, 196, .2);
		}
		else if stylus-deepdark-style == "Gruvbox-Dark" {
			--main-color: #fe8019;
			--main-background: #1d2021;
			--second-background: #282828;
			--hover-background: #3c3836;
			--main-text: #fbf1c7;
			--dimmer-text: #ebdbb2;
			--shadow: 0 1px 0.5px  rgba(60, 56, 54, .22);
		}
		else if stylus-deepdark-style == "Gruvbox-Light" {
			--main-color: #af3a03;
			--main-background: #f9f5d7;
			--second-background: #fbf1c7;
			--hover-background: #ebdbb2;
			--main-text: #282828;
			--dimmer-text: #3c3836;
			--shadow: 0 1px 0.5px  rgba(235, 219, 178, .33);
		}
		else if stylus-deepdark-style == "NierAutomata-Dark" {
			--main-color: #fe8019;
			--main-background: #33302a;
			--second-background: #48453c;
			--hover-background: #7c6f64;
			--main-text: #dad4bb;
			--dimmer-text: #bab5a1;
			--shadow: 0 1px 0.5px  rgba(124, 111, 100, .15);
		}
		else if stylus-deepdark-style == "NierAutomata-Light" {
			--main-color: #fe8019;
			--main-background: #d1cdb7;
			--second-background: #dcd8c0;
			--hover-background: #bab5a1;
			--main-text: #48453c;
			--dimmer-text: #33302a;
			--shadow: 0 1px 0.5px rgba(186, 181, 161, 2);
		}
		else if stylus-deepdark-style == "HavocOS" {
			--main-color: #0794d4;
			--main-background: #141618;
			--second-background: #1c1e20;
			--hover-background: #212528;
			--main-text: #fff;
			--dimmer-text: #b9baba;
			--shadow: 0 1px 0.5px rgba(185, 186, 186, .04);
		}
		else if stylus-deepdark-style == "Custom" {
			--main-color: mainColor;
			--main-background: mainBackground;
			--second-background: secondBackground;
			--hover-background: hoverBackground;
			--main-text: mainText;
			--dimmer-text: dimmerText;
			--shadow: 0 1px 0.5px colorShadow;
		}
	}
	/*Error page*/
	#error-page
	{
		background-color: var(--main-background) !important;
