/* ==UserStyle==
@name           duckduckgo-gruvbox-elkowar
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    gruvboxed duckduckgo
@author         ElKowar
@preprocessor   stylus
==/UserStyle== */
@-moz-document domain("duckduckgo.com") {
    :root {
        --elk-bg-dark: #1d2021;
        --elk-bg-darkisher: #202020;
        --elk-bg-darkish: #222222;
        --elk-bg-normal: #282828;
        --elk-bg-brighter: #504945;
        --elk-border: #353331;
        --elk-fg-normal: #ebdbb2;
        --elk-fg-brighter: #fbf1c7;
        --elk-fg-darker: #d5c4a1;
        --elk-fg-dark: #a89984;
        --elk-fg-inactive: #696969;
        --elk-red: #fb4934;
        --elk-purple: #b16286;
        --elk-blue: #458588;
        --elk-green: #b8bb26;
        --elk-aqua: #83c07c;
        --elk-yellow: #fabd2f;
        --elk-orange: #fe8019;
        --elk-accent: var(--elk-aqua);
    }
    body {
        background-color: var(--elk-bg-normal);
    }
    *,
    div[class*="result"],
    .result__snippet b,
    .result__date {
        color: var(--elk-fg-normal) !important;
        font-family: "xos4 terminus" !important;
        font-size: 1rem;
    }
    #duckbar a.zcm__link {
        border-bottom-color: var(--elk-fg-normal) !important;
        padding: 0 10px;
        margin: 0 0;
        transition: background-color 0.1s ease-in-out;
        &:hover {
            background-color: var(--elk-bg-dark);
        }
    }
    .switch {
        background-color: var(--elk-bg-brighter);
    }
    .zci,
    .tile-wrap,
    .metabar,
    .zci-wrap {
        background-color: var(--elk-bg-normal) !important;
        border: none !important;
    }
    .tile {
        background-color: var(--elk-bg-normal);
        box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5);
        border: none !important;
        transition: box-shadow 0.1s ease-in-out;
        &.highlight {
            box-shadow: 0 0 15px 0 rgba(0, 0, 0, 0.8) !important;
        }
    }
    .footer {
        background-color: var(--elk-bg-normal);
        display: none !important;
        .footer__card {
            background-color: var(--elk-bg-dark) !important;
            border: none;
        }
    }
    .btn.feedback-btn,
    .feedback-prompt,
    .header__clickable[data-type="showcase"],
    .header__label.social {
        display: none;
    }
    .modal__box,
    .modal__header {
        background: var(--elk-bg-normal) !important;
        & > * {
            background: var(--elk-bg-normal) !important;
        }
    }
    pre.prettyprint,
    code {
        background-color: var(--elk-bg-dark);
    }
    .module__toggle--more::after {
        background: linear-gradient(var(--elk-bg-normal), var(--elk-bg-dark)) !important;
    }
    .modal__list__link.is-highlighted {
        background-color: var(--elk-bg-brighter) !important;
    }
    .module__clickable-header > * {
        color: var(--elk-fg-normal) !important;
    }
    .btn.btn--full {
        border: none;
        background-color: var(--elk-bg-dark);
    }
    .switch__knob {
        background-color: var(--elk-fg-normal) !important;
